formulas.expression:
/* Returns the string after "Description__c":" and before the next quote */
MID(
    {!ReqAddValues},
    FIND("\"Description__c\":\"", {!ReqAddValues}) + LEN("\"Description__c\":\""),
    FIND(
        "\"",
        {!ReqAddValues},
        FIND("\"Description__c\":\"", {!ReqAddValues}) + LEN("\"Description__c\":\"")
    ) -
    (
        FIND("\"Description__c\":\"", {!ReqAddValues}) + LEN("\"Description__c\":\"")
    )
)

IF(
  CONTAINS({!ReqEditValue}, "\"Cabins_min__c\":\""),
  TEXT(
    VALUE(
      TRIM(
        SUBSTITUTE(
          MID(
            SUBSTITUTE(SUBSTITUTE({!ReqEditValue}, CHR(10), ""), CHR(13), ""),
            FIND("\"Cabins_min__c\":\"", SUBSTITUTE(SUBSTITUTE({!ReqEditValue}, CHR(10), ""), CHR(13), "")) + LEN("\"Cabins_min__c\":\""),
            FIND(
              "\"",
              SUBSTITUTE(SUBSTITUTE({!ReqEditValue}, CHR(10), ""), CHR(13), ""),
              FIND("\"Cabins_min__c\":\"", SUBSTITUTE(SUBSTITUTE({!ReqEditValue}, CHR(10), ""), CHR(13), "")) + LEN("\"Cabins_min__c\":\"")
            ) - (
              FIND("\"Cabins_min__c\":\"", SUBSTITUTE(SUBSTITUTE({!ReqEditValue}, CHR(10), ""), CHR(13), "")) + LEN("\"Cabins_min__c\":\"")
            )
          ),
          "\"",
          ""
        )
      )
    )
  ),
  ""
)

/* Returns the string after "Description__c":" and before the next quote */
MID(
    {!ReqEditValue},
    FIND("\"Description__c\":\"", {!ReqEditValue}) + LEN("\"Description__c\":\""),
    FIND(
        "\"",
        {!ReqEditValue},
        FIND("\"Description__c\":\"", {!ReqEditValue}) + LEN("\"Description__c\":\"")
    ) -
    (
        FIND("\"Description__c\":\"", {!ReqEditValue}) + LEN("\"Description__c\":\"")
    )
)

TRIM(
  MID(
    SUBSTITUTE({!ReqEditValue}, CHR(10), ""),  /* Removes line breaks */
    FIND("\"Requirement_Type__c\":\"", SUBSTITUTE({!ReqEditValue}, CHR(10), "")) + LEN("\"Requirement_Type__c\":\""),
    FIND(
      "\"",
      SUBSTITUTE({!ReqEditValue}, CHR(10), ""),
      FIND("\"Requirement_Type__c\":\"", SUBSTITUTE({!ReqEditValue}, CHR(10), "")) + LEN("\"Requirement_Type__c\":\"")
    ) - (
      FIND("\"Requirement_Type__c\":\"", SUBSTITUTE({!ReqEditValue}, CHR(10), "")) + LEN("\"Requirement_Type__c\":\"")
    )
  )
)

