CustomField.formula:
IF(
    ISBLANK( Dynamics_ID__c ),
    "",

    IF(
        CONTAINS(Dynamics_ID__c , "~"),
        RIGHT(
            Dynamics_ID__c ,
            LEN(Dynamics_ID__c ) - FIND("~", Dynamics_ID__c )
        ),

        /* No tilde, but value exists â†’ take last 36 chars */
        RIGHT(Dynamics_ID__c , 36)
    )
)
